import React, { useState } from 'react';
import './App.css';  // Você pode colocar seus estilos no arquivo CSS

function App() {
  const [query, setQuery] = useState('');
  const [operadoras, setOperadoras] = useState([]);

  // Função para buscar as operadoras
  const buscarOperadoras = async () => {
    if (query.trim() === "") {
      setOperadoras([]);
      return;
    }

    const response = await fetch(`http://localhost:3000/api/operadoras?q=${query}`);
    const data = await response.json();
    setOperadoras(data);
  };

  return (
    <div className="App">
      <header>
        <nav className="navbar">
          <div className="logo">Intuitive Care</div>
          <ul className="nav-links">
            <li><a href="./components/home.js">Início</a></li>
            <li><a href="#">Sobre</a></li>
            <li><a href="#">Serviços</a></li>
            <li><a href="#">Contato</a></li>
          </ul>
        </nav>
      </header>

      <section className="operador">
        <div className="container">
          <div className="quadrado">
            <h1>Buscar Operadoras de Saúde</h1>
            <input
              type="text"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              onInput={buscarOperadoras}
              placeholder="Digite o nome da operadora..."
            />
            <ul>
              {operadoras.length > 0 ? (
                operadoras.map((operadora) => (
                  <li key={operadora.CNPJ}>
                    <strong>{operadora.NOME_OPERADORA}</strong><br />
                    CNPJ: {operadora.CNPJ}<br />
                    Status: {operadora.STATUS_OPERADORA}
                  </li>
                ))
              ) : (
                <li>Nenhum resultado encontrado.</li>
              )}
            </ul>
          </div>
        </div>
      </section>
    </div>
  );
}

export default App;
